package com.example.androidapptp

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.View
import android.widget.Button
import android.widget.ImageView
import android.widget.Toast
import com.bumptech.glide.Glide


class MainActivity : AppCompatActivity(), View.OnClickListener {

    private lateinit var imageView: ImageView
    private lateinit var button: Button

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
        imageView = findViewById(R.id.image_view)
        button = findViewById(R.id.my_button)
        loadImage("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTdWmYyuXiL768aS-87gaP_QvbFL4AkegqRBw&usqp=CAU")
    }

    private fun loadImage(url: String) {
        Glide.with(this)
            .load(url)
            .fallback(R.drawable.ic_launcher_background)
            .into(imageView)
    }

    override fun onClick(v: View?) {
        val link = "data:image/jpeg;base64,"
        loadImage(link)
        Toast.makeText(this, "You clicked me", Toast.LENGTH_LONG).show()
    }
}